from crawler.item_handlers.EchoItemHandler import EchoItemHandler
from crawler.item_handlers.MongoDBHandler import MongoDBHandler
from crawler.AbstractCrawler import AbstractCrawler
from bs4 import BeautifulSoup
import requests

class YazdCrawler(AbstractCrawler):

    def __init__(self, item_handler):
        super().__init__(item_handler)

    def extract_aoe(self, aoe_str):
        aoe_split = aoe_str.split(" - ")
        if len(aoe_split) > 1:
            return aoe_split[1], aoe_split[0]
        else:
            return None, aoe_split[0]

    def process(self, soup: BeautifulSoup):
        for personDiv in soup.find(attrs={'class': 'YazdUniPeople'}).find_all(attrs={'class': 'row personDiv'}):
            cols = personDiv.div.find_all('div', recursive=False)[1].find_all('div', recursive=False)
            r_col = cols[1]
            l_col = cols[2]
            aoe, department = self.extract_aoe(l_col.div.find_all('div', recursive=False)[2].get_text())
            yield {
                'Name': r_col.div.div.a['title'],
                'eMail': r_col.div.find_all('div', recursive=False)[1].get_text(),
                'AOE': aoe,
                'Department': department
            }


def build_paging_data(p1, p2):
    return {
        '__EVENTTARGET': p1,
        '__VIEWSTATE': p2
    }


mongo_handler = MongoDBHandler()
mongo_handler.static_fields['College'] = "دانشگاه یزد"
yazd = YazdCrawler(mongo_handler)

yazd.crawl("https://yazd.ac.ir/faculties/science/departments/physics/people/academics")
# page 2
yazd.crawl(requests.post("https://yazd.ac.ir/faculties/science/departments/physics/people/academics", data=build_paging_data(
    "ctl15$ctl09$ctl00$rptPaging$ctl01$lbPaging",
    ""
)), fetch=False)

